package practice.loop;

public class Forloopone {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int k = 1; k<21; k++) {
			
			System.out.println(k);
		}
		for(int k = 20; k>0; k--) {
			
			System.out.println(k);
		}
		for(int k = 0; k<11; k+=2) {
			
			System.out.println(k);
		}	

	}

}
