package practice.loop;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int k = 1;
		 do {
			 System.out.println(k);
			 k++;
		 }
		while (k<10);
	
		
		
		while (k>0){
		 
			System.out.println(k);
			k--;
		
		}
	}
}
